import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/esm/Button";
import { useEffect, useState } from "react";
import { createUser } from "../services/UserService";
import { toast } from "react-toastify";

const AddUserModal = (props) => {
  const { show, handleClose, handleUpdateUsers } = props;
  const [name, setName] = useState("");
  const [job, setJob] = useState("");

  useEffect(() => {
    const keyDownHandler = (event) => {
      console.log("User pressed: ", event.key);

      if (event.key === "Enter") {
        event.preventDefault();

        // 👇️ call submit function here
        handleSave();
      }
    };

    document.addEventListener("keydown", keyDownHandler);

    return () => {
      document.removeEventListener("keydown", keyDownHandler);
    };
  }, []);

  const handleSave = async () => {
    let res = await createUser(name, job);
    console.log(res);
    if (res && res.id && res.name && res.job) {
      handleClose();
      setName("");
      setJob("");
      toast.success("Add successfully");
      handleUpdateUsers({ first_name: name, id: res.id });
    } else {
      toast.error("Add unsuccessfully");
    }
  };

  return (
    <>
      <div>
        <form>
          <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>Add New User</Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <div className="body-add-new">
                <div className="mb-3">
                  <label nextClassName="form-label">
                    <b>Name:</b>
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    placeholder="Nguyen Van A"
                    value={name}
                    onChange={(event) => setName(event.target.value)}
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">
                    <b>Job:</b>
                  </label>
                  <textarea
                    className="form-control"
                    placeholder="Student"
                    value={job}
                    onChange={(event) => setJob(event.target.value)}
                  ></textarea>
                </div>
              </div>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={handleClose}>
                Close
              </Button>
              <Button variant="primary" onClick={handleSave} type="submit">
                Save Changes
              </Button>
            </Modal.Footer>
          </Modal>
        </form>
      </div>
    </>
  );
};

export default AddUserModal;
