import "./App.scss";
import Header from "./components/Header";
import TableUsers from "./components/TableUsers";
import Container from "react-bootstrap/Container";
import Button from "react-bootstrap/Button";
import AddUserModal from "./components/AddUserModal";
import { useState } from "react";
import { ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function App() {
  const [isShowAddUserModal, setIsShowAddUserModal] = useState(false);

  const handleAddUser = () => {
    setIsShowAddUserModal(true);
  };
  const handleClose = () => {
    setIsShowAddUserModal(false);
  };

  return (
    <>
      <div className="app-container">
        <Container>
          <Header />
          <div className="my-3 add-new d-flex justify-content-between">
            <p>List Users:</p>
            <span>
              <Button variant="success mx-3" onClick={handleAddUser}>
                Add User
              </Button>

              {/* <Button variant="danger">Delete User</Button> */}
            </span>
          </div>
          <TableUsers />

          <AddUserModal show={isShowAddUserModal} handleClose={handleClose} />
        </Container>
      </div>

      <ToastContainer
        position="top-center"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />
    </>
  );
}

export default App;
