import { useEffect, useState } from "react";
import Table from "react-bootstrap/Table";
import { fetchAllUsers } from "../services/UserService";
import ReactPaginate from "react-paginate";
import AddUserModal from "./AddUserModal";
import Button from "react-bootstrap/Button";
import EditUserModal from "./EditUserModal";

const TableUsers = (props) => {
  const [listUsers, setListUsers] = useState([]);
  const [totalUsers, setTotalUsers] = useState(0);
  //Total Users should be displayed on the screen to know the total of users of database
  const [totalPages, setTotalPages] = useState(0);
  const [isShowAddUserModal, setIsShowAddUserModal] = useState(false);
  const [isShowEditUserModal, setIsShowEditUserModal] = useState(false);
  const [dataUserEdit, setDataUserEdit] = useState({});
  //display users first time
  useEffect(() => {
    getUsers(1);
  }, []);

  //display users
  const getUsers = async (page) => {
    let res = await fetchAllUsers(page);
    if (res && res.data) {
      console.log(res);
      setListUsers(res.data);
      setTotalUsers(res.total);
      setTotalPages(res.total_pages);
    }
  };

  //update users after creating new one
  const handleUpdateUsers = (user) => {
    setListUsers([user, ...listUsers]);
  };

  const handlePageClick = (event) => {
    getUsers(+event.selected + 1);
  };

  const handleClose = () => {
    setIsShowAddUserModal(false);
  };

  const handleEditUser = (user) => {
    setDataUserEdit(user);
    setIsShowEditUserModal(true);
    console.log(user);
  };
  return (
    <>
      <div>
        <div className="my-3 add-new d-flex justify-content-between">
          <p>List Users:</p>
          <span>
            <Button
              variant="success mx-3"
              onClick={() => {
                setIsShowAddUserModal(true);
              }}
            >
              Add User
            </Button>
          </span>
        </div>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>ID</th>
              <th>Email</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {listUsers &&
              listUsers.length > 0 &&
              listUsers.map((item, index) => {
                return (
                  <tr key={`user-${item.id}`}>
                    <td>{item.id}</td>
                    <td>{item.email}</td>
                    <td>{item.first_name}</td>
                    <td>{item.last_name}</td>
                    <td>
                      <Button
                        variant="warning mx-3"
                        onClick={() => handleEditUser(item)}
                      >
                        Edit
                      </Button>
                      <Button variant="danger">Delete</Button>
                    </td>
                  </tr>
                );
              })}
          </tbody>
        </Table>
        <ReactPaginate
          breakLabel="..."
          nextLabel="Next >"
          onPageChange={handlePageClick}
          pageRangeDisplayed={5}
          pageCount={totalPages}
          previousLabel="< Previous"
          renderOnZeroPageCount={null}
          containerClassName="pagination"
          activeClassName="active"
          pageLinkClassName="page-link"
          breakLinkClassName="page-link"
          nextLinkClassName="page-link"
          previousLinkClassName="page-link"
          pageClassName="page-item"
          breakClassName="page-item"
          nextClassName="page-item"
          previousClassName="page-item"
        />
        <AddUserModal
          show={isShowAddUserModal}
          handleClose={handleClose}
          handleUpdateUsers={handleUpdateUsers}
        />
        <EditUserModal show={isShowEditUserModal} dataUserEdit={dataUserEdit} />
      </div>
    </>
  );
};

export default TableUsers;
