import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/esm/Button";
import { useState } from "react";
import { postCreateUser } from "../services/UserService";
import { toast } from "react-toastify";

const AddUserModal = (props) => {
  const { show, handleClose, handleUpdateUsers } = props;
  const [firstname, setFirstname] = useState("");
  const [lastname, setLastname] = useState("");

  const handleSave = async () => {
    let res = await postCreateUser(firstname, lastname);
    console.log(res);
    if (res && res.id && res.firstname && res.lastname) {
      handleClose();
      setFirstname("");
      setLastname("");
      toast.success("Add successfully");
      handleUpdateUsers({ first_name: firstname, id: res.id });
    } else {
      toast.error("Add unsuccessfully");
    }
  };

  return (
    <>
      <div>
        <Modal
          show={show}
          onHide={handleClose}
          backdrop="static"
          keyboard={false}
        >
          <Modal.Header closeButton>
            <Modal.Title>Add New User</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <div className="body-add-new">
              <div className="mb-3">
                <label nextClassName="form-label">
                  <b>firstname:</b>
                </label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Nguyen Van A"
                  value={firstname}
                  onChange={(event) => setFirstname(event.target.value)}
                />
              </div>
              <div className="mb-3">
                <label className="form-label">
                  <b>lastname:</b>
                </label>
                <textarea
                  className="form-control"
                  placeholder="Student"
                  value={lastname}
                  onChange={(event) => setLastname(event.target.value)}
                ></textarea>
              </div>
            </div>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={() => handleSave()}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    </>
  );
};

export default AddUserModal;
